import { useEffect, useState } from 'react';
import './App.css';
import { MyMeals } from './MyMeals';
import { getAllMeals, addMeal, editMeal } from './FetchMeals';

function App() {

  const [myMeal, setMeal] = useState([]);
  const [name, setName] = useState("");
  const [editing, setEditing] = useState(false);
  const [mealId, setMealId] = useState("")


  useEffect(() => {
    getAllMeals(setMeal)
  }, [])

  const updatingInInput = (_id, name) => {
    setEditing(true)
    setName(name)
    setMealId(_id)
  }


  return (
    <div>
      <h1>Meal Plan</h1>

      <input
      type='text'
      placeholder="Add a meal"
      value = {name}
      onChange= {(e) => setName(e.target.value)}
      />

      <button onClick=
      {editing ? () => editMeal(mealId, title, setMeal, setTitle, setEditing) :
        () => addMeal(name, setName, setMeal) } >

        {editing ? "Edit" : "Add"}

      </button>
    {/*<MyMeals text="WE GOT HERE!!"/> */}

    {myMeal.map((meal) => <MyMeals text={meal.name} key={meal._id}
    updatingInInput={() => updatingInInput(meal._id, meal.name)}/>
    )}
    </div>  
  );
}
export default App;
